package b_operator;

public class Ex10_Assignment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 10;
		int b = 7;
		a= a+b;  //a+=b
		System.out.println("A="+a+" B="+b);
		a-=b;
		System.out.println("A="+a+" B="+b);
		a*=b;
		System.out.println("A="+a+" B="+b);
		a/=b;
		System.out.println("A="+a+" B="+b);
		a%=b;
		System.out.println("A="+a+" B="+b);

	}

}
